// const fs = require('fs');
const dns = require('dns')
const readline = require('readline')

const r1 = readline.createInterface({
    input: process.stdin,
    output: process.stdout,
});

r1.question('Domain Name: ', (domain) => {
    r1.close(); 

    dns.lookup(domain, (err, address) => {
        if(err) {
            console.log(err);
            return;
        }
        console.log('IP Address: %j', address);
    });

})
