const fs = require('fs');
const readline = require('readline')

const r1 = readline.createInterface({
    input: process.stdin,
    output: process.stdout,
});

r1.question('filename: ', (filename) => {
    r1.close(); 
    console.log(filename);
    fs.readFile(filename, 'utf8', (err, data) => {
        if(err) {
            console.log(err);
            return;
        }
        console.log(data.toUpperCase());
    })
})



