const fs = require('fs');
const readline = require('readline');

const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout
});


rl.question('Input filename: ', (input) => {
    rl.question('Output filename: ', (output) => {
        rl.close();
        fs.readFile(input, 'utf8', (inputErr, inputData) => {
            if(inputErr) {
                return console.log(inputErr);
            }
            let outputData = inputData.toUpperCase();
            fs.writeFile(output, outputData, (outputErr) => {
                if(outputErr) {
                    return console.log(outputErr);
                }
                return console.log('Writing fiel ' + input + ' to ' + output + ' was successful!');

            })
        });
    })   
})
